import * as actionType from '../constants/action-types';

const updateState = state => ({
  type: actionType.UPDATE_STATE,
  state,
});

const addBlockedRows = (numberOfBlockedRows, state) => ({
  type: actionType.ADD_BLOCKED_ROWS,
  numberOfBlockedRows,
  state,
});

const rotatePiece = () => ({
  type: actionType.ROTATE_PIECE,
});

const movePieceRight = () => ({
  type: actionType.MOVE_PIECE_RIGHT,
});

const movePieceDown = () => ({
  type: actionType.MOVE_PIECE_DOWN,
});

const movePieceLeft = () => ({
  type: actionType.MOVE_PIECE_LEFT,
});

const dropPieceDown = () => ({
  type: actionType.DROP_PIECE_DOWN,
});

const joinRoom = (roomName, playerName) => ({
  type: actionType.JOIN_ROOM,
  outgoing: true,
  roomName,
  playerName,
});

const startGame = () => ({
  type: actionType.START_GAME,
  outgoing: true,
});

const putPiece = piece => ({
  type: actionType.PUT_PIECE,
  outgoing: true,
  piece,
});

const finishGame = () => ({
  type: actionType.FINISH_GAME,
  outgoing: true,
});

const resetGame = () => ({
  type: actionType.RESET_GAME,
  outgoing: true,
});

export {
  updateState,
  addBlockedRows,
  rotatePiece,
  movePieceRight,
  movePieceDown,
  movePieceLeft,
  dropPieceDown,
  joinRoom,
  startGame,
  putPiece,
  finishGame,
  resetGame,
};

import React from 'react';
import ReactDom from 'react-dom';
import { store } from './store';
import { Provider } from 'react-redux';
import { App } from './containers/app';
import { parseUrl } from './url';
import { stateListener, keyPressListener } from './listeners';
import { joinRoom } from './actions';
import { isUndefined, debounce } from 'lodash';

const { roomName, playerName } = parseUrl(window.location.href);

if (!isUndefined(roomName) && !isUndefined(playerName)) {
  store.dispatch(joinRoom(roomName, playerName));
}

store.subscribe(stateListener);

ReactDom.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById('tetris')
);

document.addEventListener('keydown', debounce(keyPressListener, 150, { maxWait: 150 }));

import React from 'react';
import '../styles/Board.scss'
import { Cell } from './Cell';

//pass a 2D array.
export const Board = ({ blocks }) => (
  <div className="board">

    {blocks.map((row, y) => (
      <div className="row" key={y}>

        {row.map((cellType, x) => (
          <Cell type={cellType} key={x} />
        ))}

      </div>
    ))}

  </div>
);

import React from 'react';
import classNames from 'classnames';

const constants = {

  EMPTY : 0,

  FILLED: 1,

  BLOCKED: -1
}


export const Cell = ({ type }) => {
  const className = classNames(
    'cell',
    { 
      'empty': type === constants.EMPTY 
    },
    { 
      'filled': type === constants.FILLED
    },
    { 
      'blocked': type === constants.BLOCKED
    }
  );

  return <div className={className} />;
};

import React from 'react';

export const ControlButton = ({ title, handler }) => (

  <div className="control-button" onClick={handler}>
    <p>{title}</p>
  </div>

);

import React from 'react';
import { MainBoard } from './MainBoard';
import { StatisticsSidebar } from './StatisticsSidebar';
import { NextPiecesSidebar } from './NextPiecesSidebar';
import { StatusLine } from './StatusBar';
import { WAITING } from '../constants/player-statuses';

export const GameScreen = ({ player, board, participants, statistics, nextPieces, statusString }) => 
  (
    <>
      <StatusLine status={statusString} /> 
      <main>

        {player.status !== WAITING && <NextPiecesSidebar pieces={nextPieces} />}
        <MainBoard playerStatus={player.status} blocks={board} />
        {player.status !== WAITING && <StatisticsSidebar statistics={statistics} />}

      </main>
    </>
  );

import React, { useState, useEffect } from 'react';
import '../styles/LoadingScreen.scss';

const LoadingScreen = () => {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');

  const forcedRefreshed = event => {

    return (window.location.href = `http://127.0.0.1:3000/#${room}[${name}]`).then(window.location.reload())

  };

  return (
    //BEM
    <div className="loadingScreen">
      <p className="loadingScreen__title">_RedTetris</p>
      <form>
        <input
          className="loadingScreen_name"
          type="text"
          placeholder="Enter username"
          value={name}
          onChange={event => setName(event.target.value)}
        />
        <input
          className="loadingScreen__room"
          type="text"
          placeholder="Enter room name"
          value={room}
          onChange={event => setRoom(event.target.value)}
        />
      </form>
      <button type="submit" onClick={forcedRefreshed}>
        <p>Enter Room</p>
      </button> 
    </div>
  );
};

export { LoadingScreen };

