export { StatusLine };

export * from './piece-movements-types';
export * from './incoming-event-types';
export * from './outgoing-event-types';

const EMPTY = 0;

const FILLED = 1;

const BLOCKED = -1;

export { EMPTY, FILLED, BLOCKED };

const PENDING = 'pending';

const RUNNING = 'running';

const COMPLETED = 'completed';

export { PENDING, RUNNING, COMPLETED };

const UPDATE_STATE = 'updateState';

const ADD_BLOCKED_ROWS = 'addBlockedRows';

const USER_ERROR = 'userError';

export { UPDATE_STATE, ADD_BLOCKED_ROWS, USER_ERROR };

const JOIN_ROOM = 'joinRoom';

const START_GAME = 'startGame';

const PUT_PIECE = 'putPiece';

const FINISH_GAME = 'finishGame';

const RESET_GAME = 'resetGame';

export { JOIN_ROOM, START_GAME, PUT_PIECE, FINISH_GAME, RESET_GAME };

const ROTATE_PIECE = 'rotatePiece';

const MOVE_PIECE_RIGHT = 'movePieceRight';

const MOVE_PIECE_DOWN = 'movePieceDown';

const MOVE_PIECE_LEFT = 'movePieceLeft';

const DROP_PIECE_DOWN = 'dropPieceDown';

export { ROTATE_PIECE, MOVE_PIECE_RIGHT, MOVE_PIECE_DOWN, MOVE_PIECE_LEFT, DROP_PIECE_DOWN };

const FALLING = 'falling';

const PLACED = 'placed';

const CANNOT_BE_PLACED = 'cannotBePlaced';

export { FALLING, PLACED, CANNOT_BE_PLACED };

const WAITING = 'waiting';

const PLAYING = 'playing';

const FINISHED = 'finished';

const LEFT = 'left';

export { WAITING, PLAYING, FINISHED, LEFT };

import React from 'react';
import '../assets/styles/reset.css';
import '../assets/styles/index.scss';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { GameScreen } from '../components/GameScreen';
import { LoadingScreen } from '../components/LoadingScreen';
import { ControlButton } from '../components/ControlButton';
import { NotificationContainer } from 'react-notifications';
import {
  getPlayer,
  getBoard,
  getParticipants,
  getStatusString,
  getNextPieces,
  getStatistics,
} from '../selectors';
import { startGame, resetGame } from '../actions';
import { COMPLETED, PENDING } from '../constants/game-statuses';
import { isUndefined } from 'lodash';

const App = ({
  gameStatus,
  player,
  board,
  participants,
  statistics,
  nextPieces,
  statusString,
  buttonHandlers,
}) => {
  if (!isUndefined(player)) {
    let controlButton;
    if (player.isAdmin && buttonHandlers[gameStatus]) {
      controlButton = buttonHandlers[gameStatus];
    }

    return (
      <>
        <GameScreen
          player={player}
          board={board}
          participants={participants}
          statistics={statistics}
          nextPieces={nextPieces}
          statusString={statusString}
        />
        {controlButton && (
          <ControlButton title={controlButton.title} handler={controlButton.handler} />
        )}
        <NotificationContainer />
      </>
    );
  } else {
    return (
      <>
        <LoadingScreen />
        <NotificationContainer />
      </>
    );
  }
};

const mapStateToProps = state => {
  if (!isUndefined(state.status)) {
    const props = {
      gameStatus: state.gameStatus,
      player: getPlayer(state),
      board: getBoard(state),
      participants: getParticipants(state),
      statusString: getStatusString(state),
    };

    if (state.gameStatus !== PENDING) {
      Object.assign(props, {
        statistics: getStatistics(state),
        nextPieces: getNextPieces(state),
      });
    }

    return props;
  }
};

const mapDispatchToProps = dispatch => {
  return {
    buttonHandlers: {
      [PENDING]: {
        title: 'Start Game',
        handler: bindActionCreators(startGame, dispatch),
      },
      [COMPLETED]: {
        title: 'End Game',
        handler: bindActionCreators(resetGame, dispatch),
      },
    },
  };
};

const connectedApp = connect(mapStateToProps, mapDispatchToProps)(App);

export { connectedApp as App };

import { store } from '../store';
import {
  dropPieceDown,
  movePieceDown,
  movePieceLeft,
  movePieceRight,
  rotatePiece,
  putPiece,
  finishGame,
} from '../actions';
import { PLAYING, FINISHED } from '../constants/player-statuses';
import { FALLING, PLACED, CANNOT_BE_PLACED } from '../constants/piece-statuses';
import { cloneDeep as clone, isEqual } from 'lodash';

let interval;
let activePiece;

function stateListener() {
  const state = store.getState();

  if (state.status === PLAYING && !interval) {
    interval = setInterval(() => {
      store.dispatch(movePieceDown());
    }, 1500);
  }

  if (state.status === FINISHED && interval) {
    clearInterval(interval);
    interval = null;
  }

  if (
    state.activePiece &&
    state.activePiece.status !== FALLING &&
    !isEqual(activePiece, state.activePiece)
  ) {
    const { status } = state.activePiece;

    if (status === PLACED) {
      store.dispatch(putPiece(state.activePiece));
    } else if (status === CANNOT_BE_PLACED) {
      store.dispatch(finishGame());
    }

    activePiece = clone(state.activePiece);
  }
}

const handler = {
  ArrowUp: rotatePiece,
  ArrowRight: movePieceRight,
  ArrowDown: movePieceDown,
  ArrowLeft: movePieceLeft,
  Space: dropPieceDown,
};

function keyPressListener(event) {
  if (!['ArrowUp', 'ArrowRight', 'ArrowDown', 'ArrowLeft', 'Space'].includes(event.code)) {
    return;
  }

  event.preventDefault();

  if (event.code === 'ArrowDown') {
    clearInterval(interval);
    interval = setInterval(() => {
      store.dispatch(movePieceDown());
    }, 1500);
  }

  store.dispatch(handler[event.code]());
}

export { stateListener, keyPressListener };

import * as actionType from '../constants/action-types';
import * as pieceMovementsType from '../constants/piece-movements-types';
import { PLAYING } from '../constants/player-statuses';
import { FALLING } from '../constants/piece-statuses';

const bouncerMiddleWare = ({ getState }) => {
  return next => action => {
    const state = getState();
    if (action.type !== actionType.UPDATE_STATE && state.status !== PLAYING) {
      return;
    }

    if (
      Object.values(pieceMovementsType).includes(action.type) &&
      (!state.activePiece || state.activePiece.status !== FALLING)
    ) {
      return;
    }

    return next(action);
  };
};

export { bouncerMiddleWare };

import * as incomingEventType from '../constants/incoming-event-types';
import * as outgoingEventType from '../constants/outgoing-event-types';
import { addBlockedRows, updateState } from '../actions';
import { NotificationManager } from 'react-notifications';

const MiddleWareSockets = (socket) => {

  return ({ dispatch }) => {
    socket.on(incomingEventType.UPDATE_STATE, state => {
      dispatch(updateState(state));
    });

    socket.on(incomingEventType.ADD_BLOCKED_ROWS, ({ numberOfBlockedRows, status, board }) => {
      dispatch(addBlockedRows(numberOfBlockedRows, { status, board }));
    });

    socket.on(incomingEventType.USER_ERROR, state => {
      NotificationManager.warning(state.message, null, 5000);
    });

    return next => action => {
      if (action.outgoing) {
        switch (action.type) {
          case outgoingEventType.JOIN_ROOM: {
            const { roomName, playerName } = action;
            socket.emit(outgoingEventType.JOIN_ROOM, { roomName, playerName });
            break;
          }

          case outgoingEventType.START_GAME: {
            socket.emit(outgoingEventType.START_GAME);
            break;
          }

          case outgoingEventType.PUT_PIECE: {
            socket.emit(outgoingEventType.PUT_PIECE, action.piece);
            break;
          }

          case outgoingEventType.FINISH_GAME: {
            socket.emit(outgoingEventType.FINISH_GAME);
            break;
          }

          case outgoingEventType.RESET_GAME: {
            socket.emit(outgoingEventType.RESET_GAME);
            break;
          }
        }

        return;
      }

      return next(action);
    };
  };
};

export {MiddleWareSockets };

import * as actionType from '../constants/action-types';
import { PLAYING } from '../constants/player-statuses';
import { FALLING, PLACED, CANNOT_BE_PLACED } from '../constants/piece-statuses';
import { rotatePiece } from '../utils/rotate-piece';
import { movePieceRight } from '../utils/move-piece-right';
import { movePieceDown } from '../utils/move-piece-down';
import { movePieceLeft } from '../utils/move-piece-left';
import { renderPieceOnBoard } from '../utils/render-piece-on-board';
import { movePieceUp } from '../utils/move-piece-up';
import { isUndefined, cloneDeep as clone } from 'lodash';

const reducer = (state = {}, action) => {
  switch (action.type) {
    case actionType.UPDATE_STATE: {
      const newState = Object.assign(clone(state), action.state);

      if (newState.board) {
        let renderedBoard;

        if (
          newState.status === PLAYING &&
          newState.activePiece &&
          newState.activePiece.status === FALLING
        ) {
          renderedBoard = renderPieceOnBoard(newState.activePiece, newState.board);
          if (isUndefined(renderedBoard)) {
            newState.activePiece.status = CANNOT_BE_PLACED;
            renderedBoard = clone(newState.board);
          }
        } else {
          renderedBoard = clone(newState.board);
        }
        Object.assign(newState, { renderedBoard });
      }

      return newState;
    }

    case actionType.ADD_BLOCKED_ROWS: {
      if (action.state.status === PLAYING) {
        const activePiece = movePieceUp(state.activePiece, action.numberOfBlockedRows);

        let renderedBoard = renderPieceOnBoard(activePiece, action.state.board);
        if (isUndefined(renderedBoard)) {
          activePiece.status = CANNOT_BE_PLACED;
          renderedBoard = clone(action.state.board);
        }

        return Object.assign(clone(state), action.state, {
          renderedBoard,
          activePiece,
        });
      } else {
        const { activePiece } = state;

        return Object.assign(clone(state), {
          activePiece: Object.assign(clone(activePiece), {
            status: activePiece.status === FALLING ? CANNOT_BE_PLACED : activePiece.status,
          }),
          status: action.state.status,
          board: action.state.board,
          renderedBoard: clone(action.state.board),
        });
      }
    }

    case actionType.ROTATE_PIECE: {
      const activePiece = rotatePiece(state.activePiece);
      const renderedBoard = renderPieceOnBoard(activePiece, state.board);

      if (isUndefined(renderedBoard)) {
        return state;
      }

      return Object.assign(clone(state), {
        activePiece,
        renderedBoard,
      });
    }

    case actionType.MOVE_PIECE_RIGHT: {
      const activePiece = movePieceRight(state.activePiece);
      const renderedBoard = renderPieceOnBoard(activePiece, state.board);

      if (isUndefined(renderedBoard)) {
        return state;
      }

      return Object.assign(clone(state), {
        activePiece,
        renderedBoard,
      });
    }

    case actionType.MOVE_PIECE_DOWN: {
      const activePiece = movePieceDown(state.activePiece);
      const renderedBoard = renderPieceOnBoard(activePiece, state.board);

      if (isUndefined(renderedBoard)) {
        return Object.assign(clone(state), {
          activePiece: Object.assign(clone(state.activePiece), {
            status: PLACED,
          }),
        });
      }

      return Object.assign(clone(state), {
        activePiece,
        renderedBoard,
      });
    }

    case actionType.MOVE_PIECE_LEFT: {
      const activePiece = movePieceLeft(state.activePiece);
      const renderedBoard = renderPieceOnBoard(activePiece, state.board);

      if (isUndefined(renderedBoard)) {
        return state;
      }

      return Object.assign(clone(state), {
        activePiece,
        renderedBoard,
      });
    }

    case actionType.DROP_PIECE_DOWN: {
      let { activePiece, renderedBoard } = state;
      let nextActivePiece = movePieceDown(state.activePiece);
      let nextRenderedBoard = renderPieceOnBoard(nextActivePiece, state.board);

      while (!isUndefined(nextRenderedBoard)) {
        activePiece = nextActivePiece;
        renderedBoard = nextRenderedBoard;
        nextActivePiece = movePieceDown(activePiece);
        nextRenderedBoard = renderPieceOnBoard(nextActivePiece, state.board);
      }

      activePiece.status = PLACED;
      return Object.assign(clone(state), {
        activePiece,
        renderedBoard,
      });
    }

    default: {
      return state;
    }
  }
};

export { reducer };

const getBoard = ({ renderedBoard }) => {
  return renderedBoard;
};

export { getBoard };

import { renderPieceOnBoard } from '../utils/render-piece-on-board';
import { EMPTY } from '../../server/constants/cell-types';
import { cloneDeep as clone } from 'lodash';

const SIDE_LENGTH = 4;

const createEmptyBlocks = (rows, columns) => {
  return Array.from(Array(rows), () => new Array(columns).fill(EMPTY));
};

const getNormalizedPieceBlocks = blocks => {
  const normalizedBlocks = createEmptyBlocks(SIDE_LENGTH, SIDE_LENGTH);

  const clearedBlocks = blocks.filter(row => row.some(cell => cell !== EMPTY));
  const piece = Object.assign(
    {
      blocks: clone(clearedBlocks),
    },
    {
      y: Math.floor((normalizedBlocks.length - clearedBlocks.length) / 2),
      x: Math.ceil((normalizedBlocks[0].length - clearedBlocks[0].length) / 2),
    }
  );

  return renderPieceOnBoard(piece, normalizedBlocks);
};

const getNextPieces = ({ nextPieces }) => {
  return nextPieces.map(({ blocks }) => getNormalizedPieceBlocks(blocks));
};

export { getNextPieces };

const getParticipants = ({ playersList }) => {
  return playersList;
};

export { getParticipants };

const getPlayer = state => {
  return {
    id: state.id,
    isAdmin: state.isAdmin,
    status: state.status,
  };
};

export { getPlayer };

import converter from 'number-to-words';

const getStatistics = ({ activePiece, id: playerId, playersList }) => {
  const statisticBlocks = [];

  const player = playersList.find(({ id }) => id === playerId);

  statisticBlocks.push({
    value: converter.toOrdinal(player.position + 1),
    caption: 'position',
  });

  statisticBlocks.push({
    value: player.score,
    caption: 'points',
  });

  statisticBlocks.push({
    value: activePiece.index,
    caption: 'pieces',
  });

  return statisticBlocks;
};

export { getStatistics };

import converter from 'number-to-words';
import { PENDING, RUNNING, COMPLETED } from '../constants/game-statuses';
import { PLAYING } from '../constants/player-statuses';

const getStatusString = ({ gameStatus, id: playerId, playersList }) => {
  switch (gameStatus) {
    case PENDING: {
      if (playersList.length === 1) {
        return "There's only You in The Room";
      } else {
        return `${playersList.length} players joined!`;
      }
    }

    case RUNNING: {
      const player = playersList.find(({ id }) => id === playerId);

      if (player.status === PLAYING) {
        return 'Game In Progress';
      } else {
        const playingOpponents = playersList.filter(({ status }) => status === PLAYING);

        if (playingOpponents.length === 1) {
          return 'There Are People or A Person still Playing';
        } else {
          return `${playingOpponents.length} opponents are still playing the game`;
        }
      }
    }

    case COMPLETED: {
      const player = playersList.find(({ id }) => id === playerId);

      if (player.position === 0) {
        return 'You won the game';
      } else {
        return ` You Came ${converter.toOrdinal(player.position + 1)}`;
      }
    }
  }
};

export { getStatusString };

export * from '../selectors/get-player';
export * from '../selectors/get-board';
export * from '../selectors/get-participants';
export * from '../selectors/get-status-string';
export * from '../selectors/get-next-pieces';
export * from '../selectors/get-statistics';

import io from 'socket.io-client';
import { applyMiddleware, createStore } from 'redux';
import { reducer } from './reducers';
import { MiddleWareSockets} from './middlewares/socket-middleware';
import { bouncerMiddleWare } from './middlewares/bouncer-middleware';
import thunk from 'redux-thunk';
import logger from 'redux-logger';

const socket = io.connect();

const store = createStore(
  reducer,
  applyMiddleware(thunk, MiddleWareSockets(socket), bouncerMiddleWare, logger)
);

export { store };

import parse from 'url-parse';
import decodeUriComponent from 'decode-uri-component';

const parseUrl = url => {
  const { hash } = parse(decodeUriComponent(url));
  const hashRegExp = /#(.+)\[(.+)]/;
  if (!hashRegExp.test(hash)) return {};
  const [, roomName, playerName] = hash.match(hashRegExp);
  return {
    roomName,
    playerName,
  };
};

export { parseUrl };

import { cloneDeep as clone } from 'lodash';

const movePieceDown = piece => {
  return Object.assign(clone(piece), {
    y: piece.y + 1,
  });
};

export { movePieceDown };

import { cloneDeep as clone } from 'lodash';

const movePieceLeft = piece => {
  return Object.assign(clone(piece), {
    x: piece.x - 1,
  });
};

export { movePieceLeft };

import { cloneDeep as clone } from 'lodash';

const movePieceRight = piece => {
  return Object.assign(clone(piece), {
    x: piece.x + 1,
  });
};

export { movePieceRight };

import { EMPTY } from '../../server/constants/cell-types';
import { cloneDeep as clone } from 'lodash';

const movePieceUp = (piece, step) => {
  return Object.assign(clone(piece), {
    y:
      Math.max(piece.y - step, -piece.blocks.findIndex(row => row.some(cell => cell !== EMPTY))) ||
      0,
  });
};

export { movePieceUp };

import { EMPTY, FILLED } from '../constants/cell-types';
import { cloneDeep as clone, isUndefined } from 'lodash';

const renderPieceOnBoard = (piece, board) => {
  const newBoard = clone(board);
  for (let y = 0; y < piece.blocks.length; y++) {
    for (let x = 0; x < piece.blocks[0].length; x++) {
      if (piece.blocks[y][x] === FILLED) {
        if (
          isUndefined(board[piece.y + y]) ||
          isUndefined(board[piece.y + y][piece.x + x]) ||
          board[piece.y + y][piece.x + x] !== EMPTY
        ) {
          return;
        }
        newBoard[piece.y + y][piece.x + x] = piece.blocks[y][x];
      }
    }
  }
  return newBoard;
};

